import React, { useEffect, useState } from "react";
import Swal from "sweetalert2";

const Home = () => {
    const [userList, setUserList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editUser, setEditUser] = useState(null);
    const [showPassword, setShowPassword] = useState(false);

    useEffect(() => {
        // Fetch user data from the server
        // Replace the URL and headers with your API endpoint and authentication logic
        fetch("http://localhost:8000/user/", {
            method: "GET",
            headers: {
                "Authorization": "Bearer " + sessionStorage.getItem("jwttoken"),
            },
        })
            .then((res) => res.json())
            .then((data) => {
                setUserList(data);
                setLoading(false);

                // Show welcome SweetAlert with the user's name
                const loggedInUserName = data.find((user) => user.id === parseInt(sessionStorage.getItem("userId")))?.name || "Unknown User";
                Swal.fire({
                    icon: "success",
                    title: `Welcome, ${loggedInUserName}!`,
                    showConfirmButton: false,
                    timer: 2000,
                });
            })
            .catch((error) => {
                console.error("Error fetching user data:", error);
                setLoading(false);
            });
    }, []);

    const handleEdit = (userId) => {
        const userToEdit = userList.find((user) => user.id === userId);
        setEditUser(userToEdit);

        // Display SweetAlert for editing
        Swal.fire({
            title: 'Edit User',
            html: (
                <form>
                    <div className="form-group">
                        <label htmlFor="editName">Name</label>
                        <input
                            type="text"
                            className="form-control"
                            id="editName"
                            name="name"
                            value={editUser?.name || ""}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="editEmail">Email</label>
                        <input
                            type="email"
                            className="form-control"
                            id="editEmail"
                            name="email"
                            value={editUser?.email || ""}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="editPassword">Password</label>
                        <input
                            type={showPassword ? "text" : "password"}
                            className="form-control"
                            id="editPassword"
                            name="password"
                            value={editUser?.password || ""}
                            onChange={handleInputChange}
                        />
                    </div>
                </form>
            ),
            showCancelButton: true,
            confirmButtonText: 'Save',
            cancelButtonText: 'Cancel',
        }).then((result) => {
            if (result.isConfirmed) {
                // Perform the edit operation on the server
                // After a successful edit, update the user list and show a SweetAlert
                fetch(`http://localhost:8000/user/${userId}`, {
                    method: "PUT",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": "Bearer " + sessionStorage.getItem("jwttoken"),
                    },
                    body: JSON.stringify(editUser),
                })
                    .then(() => {
                        setUserList((prevUsers) =>
                            prevUsers.map((user) => (user.id === editUser.id ? editUser : user))
                        );

                        // Show SweetAlert for successful edit
                        Swal.fire({
                            icon: "success",
                            title: "User Edited!",
                            showConfirmButton: false,
                            timer: 1500,
                        });
                    })
                    .catch((error) => {
                        console.error("Error editing user:", error);

                        // Show SweetAlert for unsuccessful edit
                        Swal.fire({
                            icon: "error",
                            title: "Error Editing User",
                            text: "An error occurred while editing the user.",
                        });
                    });
            }
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditUser((prevUser) => ({ ...prevUser, [name]: value }));
    };

    const toggleShowPassword = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Edit dan Delete Akun</h1>

            <div className="table-responsive">
                <table className="table table-bordered table-striped text-center">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Password</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {userList.map((user) => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{showPassword ? user.password : "********"}</td>
                                <td>
                                    <button onClick={() => handleEdit(user.id)} className="btn btn-primary">
                                        Edit
                                    </button>
                                    {/* (Rest of the code for the "Delete" button remains unchanged) */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Home;
