import React, { useEffect, useState } from "react";
import Swal from "sweetalert2";

const Home = () => {
  const [userList, setUserList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);
  const [editUser, setEditUser] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    // Fetch user data from the server
    // Replace the URL and headers with your API endpoint and authentication logic
    fetch("http://localhost:8000/user/", {
      method: "GET",
      headers: {
        Authorization: "Bearer " + sessionStorage.getItem("jwttoken"),
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setUserList(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        setLoading(false);
      });
  }, []);

  const handleEdit = (userId) => {
    const userToEdit = userList.find((user) => user.id === userId);
    setEditUser(userToEdit);
    setShowEditModal(true);
    setShowPassword(false);
  };

  const closeEditModal = () => {
    setEditUser(null);
    setShowEditModal(false);
  };

  const handleEditSubmit = () => {
    // Perform the edit operation on the server
    // After a successful edit, update the user list and show a SweetAlert
    fetch(`http://localhost:8000/user/${editUser.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + sessionStorage.getItem("jwttoken"),
      },
      body: JSON.stringify(editUser),
    })
      .then(() => {
        setUserList((prevUsers) =>
          prevUsers.map((user) => (user.id === editUser.id ? editUser : user))
        );
        setEditUser(null);
        setShowEditModal(false);
        // Show SweetAlert for successful edit
        Swal.fire({
          icon: "success",
          title: "User Edited!",
          showConfirmButton: false,
          timer: 1500,
        });
      })
      .catch((error) => {
        console.error("Error editing user:", error);
        setEditUser(null);
        setShowEditModal(false);
        // Show SweetAlert for unsuccessful edit
        Swal.fire({
          icon: "error",
          title: "Error Editing User",
          text: "An error occurred while editing the user.",
        });
      });
  };

  const handleEditCancel = () => {
    setEditUser(null);
    setShowEditModal(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditUser((prevUser) => ({ ...prevUser, [name]: value }));
  };

  const handleDelete = (userId) => {
    setDeleteConfirmation({
      userId,
      name: userList.find((user) => user.id === userId)?.name || "Unknown User",
    });
  };

  const confirmDelete = () => {
    const { userId } = deleteConfirmation;
    // Perform the delete operation on the server
    // After a successful deletion, update the user list and show a SweetAlert
    fetch(`http://localhost:8000/user/${userId}`, {
      method: "DELETE",
      headers: {
        Authorization: "Bearer " + sessionStorage.getItem("jwttoken"),
      },
    })
      .then(() => {
        setUserList((prevUsers) =>
          prevUsers.filter((user) => user.id !== userId)
        );
        setDeleteConfirmation(null);
        // Show SweetAlert for successful deletion
        Swal.fire({
          icon: "success",
          title: "User Deleted!",
          showConfirmButton: false,
          timer: 1500,
        });
      })
      .catch((error) => {
        console.error("Error deleting user:", error);
        setDeleteConfirmation(null);
        // Show SweetAlert for unsuccessful deletion
        Swal.fire({
          icon: "error",
          title: "Error Deleting User",
          text: "An error occurred while deleting the user.",
        });
      });
  };

  const cancelDelete = () => {
    setDeleteConfirmation(null);
  };

  const toggleShowPassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  return (
    <div>
      <h1 className="text-center">User Management</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Password</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {userList.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{showPassword ? user.password : "********"}</td>
                  <td>
                    <button
                      onClick={() => handleEdit(user.id)}
                      className="btn btn-primary"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(user.id)}
                      className="btn btn-danger"
                      style={{ marginLeft: "1%" }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {showEditModal && (
            <div
              className="modal"
              style={{
                display: "block",
                backgroundColor: "rgba(0,0,0,0.5)",
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                zIndex: 1,
              }}
            >
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Edit User</h5>
                    <button
                      type="button"
                      className="btn-close btn-close-white"
                      aria-label="Close"
                      onClick={handleEditCancel}
                    ></button>
                  </div>
                  <div className="modal-body">
                    <form>
                      <div className="form-group">
                        <label htmlFor="editName">Name</label>
                        <input
                          type="text"
                          className="form-control"
                          id="editName"
                          name="name"
                          value={editUser?.name || ""}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="editEmail">Email</label>
                        <input
                          type="email"
                          className="form-control"
                          id="editEmail"
                          name="email"
                          value={editUser?.email || ""}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="editPassword">Password</label>
                        <input
                          type={showPassword ? "text" : "password"}
                          className="form-control"
                          id="editPassword"
                          name="password"
                          value={editUser?.password || ""}
                          onChange={handleInputChange}
                        />
                      </div>
                      <button
                        type="button"
                        className="btn btn-success"
                        onClick={handleEditSubmit}
                      >
                        Save
                      </button>
                      <button
                        type="button"
                        className="btn btn-secondary"
                        onClick={handleEditCancel}
                        style={{ marginLeft: "1%" }}
                      >
                        Cancel
                      </button>
                      <button
                        type="button"
                        className="btn btn-info"
                        onClick={toggleShowPassword}
                        style={{ marginLeft: "1%" }}
                      >
                        {showPassword ? "Hide Password" : "Show Password"}
                      </button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          )}

          {deleteConfirmation && (
            <div
              className="modal"
              style={{
                display: "block",
                backgroundColor: "rgba(0,0,0,0.5)",
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                zIndex: 1,
              }}
            >
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Confirm Delete</h5>
                    <button
                      type="button"
                      className="close"
                      onClick={cancelDelete}
                    >
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div className="modal-body">
                    <p>
                      Are you sure you want to delete the user{" "}
                      {deleteConfirmation.name}?
                    </p>
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={cancelDelete}
                    >
                      Cancel
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={confirmDelete}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Home;
