import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Register = () => {
    const [id, idchange] = useState("");
    const [name, namechange] = useState("");
    const [password, passwordchange] = useState("");
    const [email, emailchange] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [homeAddress, setHomeAddress] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [countryCode, setCountryCode] = useState("+62"); // Default country code for Indonesia

    const navigate = useNavigate();

    const IsValidate = () => {
        let isproceed = true;
        let errormessage = 'Please enter the value in ';
        // ... (same validation logic as before)

        if (!isproceed) {
            toast.warning(errormessage);
        } else {
            if (!/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email)) {
                isproceed = false;
                toast.warning('Please enter a valid email');
            }
        }
        return isproceed;
    };

    const handlesubmit = (e) => {
        e.preventDefault();
        let regobj = { id, name, password, email, phoneNumber, homeAddress, postalCode };
        if (IsValidate()) {
            // Append the country code to the phone number before sending to the server
            regobj.phoneNumber = countryCode + regobj.phoneNumber;

            fetch("http://localhost:8000/user", {
                method: "POST",
                headers: { 'content-type': 'application/json' },
                body: JSON.stringify(regobj),
            }).then((res) => {
                toast.success('Registered successfully.');
                navigate('/login');
            }).catch((err) => {
                toast.error('Failed :' + err.message);
            });
        }
    };

    return (
        <div className="row">
            <div className="offset-lg-3 col-lg-6" style={{ marginTop: '100px' }}>
                <form className="container" onSubmit={handlesubmit}>
                    <div className="card">
                        <div className="card-header">
                            <h1>User Registration</h1>
                        </div>
                        <div className="card-body">
                            {/* ... (other form fields) */}
                            <div className="col-lg-6">
                                <div className="form-group">
                                    <label>Country Code</label>
                                    <select
                                        className="form-control"
                                        value={countryCode}
                                        onChange={(e) => setCountryCode(e.target.value)}
                                    >
                                        <option value="+62">Indonesia (+62)</option>
                                        {/* Add more options for other countries as needed */}
                                    </select>
                                </div>
                            </div>
                            <div className="col-lg-6">
                                <div className="form-group">
                                    <label>Phone Number <span className="errmsg">*</span></label>
                                    <input value={phoneNumber} onChange={e => setPhoneNumber(e.target.value)} className="form-control" />
                                </div>
                            </div>
                            {/* ... (other form fields) */}
                        </div>
                        <div className="card-footer">
                            <button type="submit" className="btn btn-primary">Register</button>
                            <Link to={'/login'} className="btn btn-success" style={{ marginLeft: '1%' }}>Login</Link>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Register;
