import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

const Home = () => {
  const navigate = useNavigate();
  const [userList, setUserList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);

  useEffect(() => {
    // Fetch user data from the server
    // Replace the URL and headers with your API endpoint and authentication logic
    fetch("http://localhost:8000/user/", {
      method: "GET",
      headers: {
        Authorization: "Bearer " + sessionStorage.getItem("jwttoken"),
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setUserList(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        setLoading(false);
      });
  }, []);

  const handleEdit = (userId) => {
    // Redirect to the edit page with the userId as a parameter
    navigate(`/edit-user/${userId}`);
  };

  const handleDelete = (userId) => {
    // Set up a confirmation dialog before performing the delete operation
    setDeleteConfirmation({
      userId,
      username:
        userList.find((user) => user.id === userId)?.name || "Unknown User",
    });
  };

  const confirmDelete = () => {
    const { userId } = deleteConfirmation;
    // Perform the delete operation on the server
    // After a successful deletion, update the user list and show a SweetAlert
    fetch(`http://localhost:8000/user/${userId}`, {
      method: "DELETE",
      headers: {
        Authorization: "Bearer " + sessionStorage.getItem("jwttoken"),
      },
    })
      .then(() => {
        setUserList((prevUsers) =>
          prevUsers.filter((user) => user.id !== userId)
        );
        setDeleteConfirmation(null);
        // Show SweetAlert for successful deletion
        Swal.fire({
          icon: "success",
          title: "User Deleted!",
          showConfirmButton: false,
          timer: 1500,
        });
      })
      .catch((error) => {
        console.error("Error deleting user:", error);
        setDeleteConfirmation(null);
        // Show SweetAlert for unsuccessful deletion
        Swal.fire({
          icon: "error",
          title: "Error Deleting User",
          text: "An error occurred while deleting the user.",
        });
      });
  };

  const cancelDelete = () => {
    // Close the confirmation dialog without performing the delete operation
    setDeleteConfirmation(null);
  };

  return (
    <div>
      <h1 className="text-center">User Management</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-bordered">
          <thead>
            <tr>
              <td>Username</td>
              <td>Name</td>
              <td>Email</td>
              <td>Actions</td>
            </tr>
          </thead>
          <tbody>
            {userList.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button
                    onClick={() => handleEdit(user.id)}
                    className="btn btn-primary"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(user.id)}
                    className="btn btn-danger"
                    style={{ marginLeft: "1%" }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Delete Confirmation Dialog */}
      {deleteConfirmation && (
        <div
          className="modal"
          style={{
            display: "block",
            backgroundColor: "rgba(0,0,0,0.5)",
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            zIndex: 1,
          }}
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Delete</h5>
                <button type="button" className="close" onClick={cancelDelete}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>
                  Are you sure you want to delete the user{" "}
                  {deleteConfirmation.username}?
                </p>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={cancelDelete}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={confirmDelete}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
