import React, { useEffect, useState } from "react";
import Swal from "sweetalert2";

const Home = () => {
    const [userList, setUserList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editUser, setEditUser] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    useEffect(() => {
        // Fetch user data from the server
        // Replace the URL and headers with your API endpoint and authentication logic
        fetch("http://localhost:8000/user/", {
            method: "GET",
            headers: {
                "Authorization": "Bearer " + sessionStorage.getItem("jwttoken"),
            },
        })
            .then((res) => res.json())
            .then((data) => {
                setUserList(data);
                setLoading(false);

                // Show welcome SweetAlert with the user's name
                const loggedInUserName = data.find((user) => user.id === parseInt(sessionStorage.getItem("userId")))?.name || "Unknown User";
                Swal.fire({
                    icon: "success",
                    title: `Welcome, ${loggedInUserName}!`,
                    showConfirmButton: false,
                    timer: 2000,
                });
            })
            .catch((error) => {
                console.error("Error fetching user data:", error);
                setLoading(false);
            });
    }, []);

    const handleEdit = (userId) => {
        const userToEdit = userList.find((user) => user.id === userId);
        setEditUser(userToEdit);
        setShowEditModal(true);
        setShowPassword(false);
    };

    const closeEditModal = () => {
        setEditUser(null);
        setShowEditModal(false);
    };

    const handleEditSubmit = () => {
        // ... (Rest of the edit functionality remains unchanged)
    };

    const handleEditCancel = () => {
        setEditUser(null);
        setShowEditModal(false);
    };

    const handleInputChange = (e) => {
        // ... (Rest of the input change handling remains unchanged)
    };

    const handleDelete = (userId) => {
        // Display SweetAlert for confirmation
        Swal.fire({
            icon: 'warning',
            title: 'Are you sure?',
            text: `You are about to delete the user with ID ${userId}.`,
            showCancelButton: true,
            confirmButtonText: 'Delete',
            cancelButtonText: 'Cancel',
        }).then((result) => {
            if (result.isConfirmed) {
                // Perform the delete operation on the server
                // After a successful deletion, update the user list and show a SweetAlert
                fetch(`http://localhost:8000/user/${userId}`, {
                    method: "DELETE",
                    headers: {
                        "Authorization": "Bearer " + sessionStorage.getItem("jwttoken"),
                    },
                })
                    .then(() => {
                        setUserList((prevUsers) => prevUsers.filter((user) => user.id !== userId));
                        // Show SweetAlert for successful deletion
                        Swal.fire({
                            icon: "success",
                            title: "User Deleted!",
                            showConfirmButton: false,
                            timer: 1500,
                        });
                    })
                    .catch((error) => {
                        console.error("Error deleting user:", error);
                        // Show SweetAlert for unsuccessful deletion
                        Swal.fire({
                            icon: "error",
                            title: "Error Deleting User",
                            text: "An error occurred while deleting the user.",
                        });
                    });
            }
        });
    };

    const toggleShowPassword = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return (
        <div className="container mt-5">
            {/* ... (Rest of the JSX remains unchanged) */}
        </div>
    );
};

export default Home;
